// Contador de alumnos corregidos exitosamente en ESTA ejecución
// Solo cuenta los items que tienen el campo 'corregido_ahora' = true

const todosLosItems = $input.all();
const itemsCorregidosAhora = todosLosItems.filter(item => item.json.corregido_ahora === true);
const cantidadCorregidos = itemsCorregidosAhora.length;

return [
  { 
    json: { 
      cantidad_corregidos: cantidadCorregidos,
      total_procesados: todosLosItems.length
    } 
  }
];

// Parser de respuesta de Gemini

const geminiResponse = $input.first().json;

try {
  // La respuesta puede venir directamente como array o dentro de una propiedad
  let responseArray;

  // Intentar diferentes estructuras posibles
  if (Array.isArray(geminiResponse)) {
    responseArray = geminiResponse;
  } else if (geminiResponse.body && Array.isArray(geminiResponse.body)) {
    responseArray = geminiResponse.body;
  } else if (geminiResponse.data && Array.isArray(geminiResponse.data)) {
    responseArray = geminiResponse.data;
  } else {
    // Si no es array, intentar acceder directamente
    responseArray = [geminiResponse];
  }

  // Navegar a la estructura de respuesta de Gemini
  const candidate = responseArray[0]?.candidates?.[0];

  if (!candidate) {
    // Debug: mostrar la estructura recibida
    throw new Error(`No se encontró candidate. Estructura recibida: ${JSON.stringify(geminiResponse).substring(0, 200)}`);
  }

  const textContent = candidate.content?.parts?.[0]?.text;

  if (!textContent) {
    throw new Error("No se encontró texto en la respuesta");
  }

  // Parsear el JSON que viene como string dentro de text
  const parsedData = JSON.parse(textContent);

  // Extraer las propiedades necesarias
  const resultado = {
    alumno: $('Contexto Alumno2').first().json.student_name || "",
    puntaje_total: parsedData.puntaje_total || 0,
    criterios: parsedData.criterios || "",
    fortalezas: parsedData.fortalezas || "",
    recomendaciones: parsedData.recomendaciones || ""
  };

  return [{ json: resultado }];

} catch (error) {
  return [{ 
    json: { 
      error: "Error al parsear respuesta de Gemini",
      detalle: error.message,
      debug_input: JSON.stringify(geminiResponse).substring(0, 500), // Primeros 500 caracteres para debug
      alumno: "",
      puntaje_total: 0,
      criterios: "",
      fortalezas: "",
      recomendaciones: ""
    } 
  }];
}
